<?phpif($handle = opendir("plugins/")) {	while (false !== ($entry = readdir($handle))) {		if($entry != "." && $entry != "..") {			if(file_exists("plugins/".$entry."/init.php")){				$plugin_enabled = false;				include("plugins/".$entry."/init.php");				if($plugin_enabled == true){					if(file_exists("plugins/".$entry."/main.php")){						include("plugins/".$entry."/main.php");					}else{						echo sprintf(_t("plugin \"%s\" is not valid. Please re-install this plugin, including the main.php.</br>"), $entry);					}				}			}else{				if(DEVELOPMODE){					echo sprintf(_t("plugin \"%s\" is not valid. Please re-install this plugin, including the init.php.</br>"), $entry);				}			}		}	}	closedir($handle);}?>