month"] = $diff->format('%m'); 			$countdown["day"] = $diff->format('%d'); 			$countdown["hour"] = $diff->format('%h'); 			$countdown["minute"] = $diff->format('%i'); 			$countdown["secound"] = $diff->format('%s'); 						foreach($countdown as $key => $value){				if(count($output) < 3){					if($value == 0 && count($output) == 0){										}else{						if($value > 1 || $value == 0){							$output[] = "<div class='countdown_elem well' id='countdown_elem_$key'><span>$value</span> ".$key."s</div>";						}else{							$output[] = "<div class='countdown_elem well' id='countdown_elem_$key'><span>$value</span> $key</div>";						}					}				}else{					if($value > 1){						$output[] = "<div class='countdown_elem well' id='countdown_elem_$key' style='display: none;'><span>$value</span> ".$key."s</div>";					}else{						$output[] = "<div class='countdown_elem well' id='countdown_elem_$key' style='display: none;'><span>$value</span> $key</div>";					}				}			}			echo implode("", $output);		}		print_r(timediff($wip_task_info["deadline"]));		?>	</div>	<hr>	<div class="panel panel-success">		<div class="panel-heading">Assigned to <strong><?php echo $wip_task_info["assigned_toUSERNAME"]; ?></strong></div>		<div class="panel-body">		<b><?php echo $wip_task_info["task-name"]; ?></b></br>		<?php echo $wip_task_info["description"]; ?>		</div>	</div>	<hr>	<h4 style="text-align: center;"> Do you want to <a href="#" data-toggle="modal" data-target="#myModal">stay connected</a> to this project?</h4><div style="text-align: center; margin-top: 15px; ">	<h2><a href="<?php echo ROOT; ?>" class="btn btn-primary btn-lg" role="button">take me home</a></h2></div><!-- Modal --><div class="modal fade" id="myModal" tabindex="-1" role="dialog" aria-labelledby="myModalLabel" aria-hidden="true">	<div class="modal-dialog">		<div class="modal-content">			<div class="modal-header">					<button type="button" class="close" data-dismiss="modal" aria-hidden="true">&t