<?phpif(!ROOT){exit;}?><div class="snippet-box" style="height: 710px;"><?php$snippet = new mysql();$snippet_result = $snippet->query("SELECT * FROM `ecommerce_w`;");while($snippet_arr[] = $snippet->result($snippet_result, "assoc"));foreach($snippet_arr as $key => $ecommerce){if($ecommerce != ""){?><div class="snippet" style="width: 270px; height: 350px; overflow: hidden; float: left; margin: 5px; display: block;"><div class="snippet-heading" style="color: rgb(255, 145, 0); height: 60px"><h1><?php echo $ecommerce["Heading"]; ?></h1></div><div class="snippet-smline" style="width: 100%; height: 20px;">	<div class="snippet-author" style="float: left;"><small style="font-size: 0.8em; color: rgb(66,66,66);">by <?php echo $ecommerce["author"]; ?></small></div>	<div class="snippet-label" style="float: right;">	<small style="font-size: 0.8em; color: rgb(66,66,66);">	<?php	if($ecommerce["label"] == "new"){	?>	<span class="label label-primary">New</span>	<?php	}elseif($ecommerce["label"] == "recommended"){	?>	<span class="label label-success">Recommended</span>	<?php	}elseif($ecommerce["label"] == "hot"){	?>	<span class="label label-danger">hot</span>	<?php	}	?>	</small></div></div><div class="snippet-content" style="marign-top: 5px; border: 1px solid rgba(166, 166, 166, 0.4); background-color: white;"><img class="snippet-thumnail" src="<?php echo $ecommerce["img-path"]; ?>" width="32px" height="32px"/><div class="snippet-price"><span class="snippet-pricing"><?php echo $ecommerce["price"]; ?></span></div><div class="snippet-seemore btn btn-link"><a href="<?php echo ROOT_URL.$ecommerce["affect_pageNAME"]; ?>"><?php echo _t("See more");?></a></div></div></div><?php}}?></div>