<?phpif(can_current_user("create_post")){?><div class="element"><div class="element-heading"><span><?php echo _t("create a new text");?></span></div><div class="element-content"><form><label for="post_title"><?php echo _t("title"); ?></label><input type="text" id="post_title" placeholder="<?php echo _t("title"); ?>"/></form></div></div><?php}?><div class="element"><div class="element-heading"><span><?php echo _t("manage old posts");?></span></div><div class="element-content"><?php$postsql = new mysql();$postresult = $postsql->query("SELECT * FROM  `".$postsql->dbprae."text_w`;");while($posts[] = $postsql->result($postresult, "assoc"));?><div id="change_post"><ul class="selector-list well"><?phpforeach($posts as $post){?>	<li class="selector-element">		<a href="<?php echo $post["ID"]; ?>" data-target="post-<?php echo $post["ID"];?>" class="selector-link"><span class="selector-post"><?php echo $post["Heading"]; ?></span></a>		<span class="selector-path label-warning" ><?php echo $post["label"]; ?></span>	</li><?php}?></ul></div><div id="post_edit_field"><?phpforeach($posts as $key => $post){if($post != ""){?><div id="post-<?php echo $post["ID"];?>" <?php echo ($key != 0) ? "style='display: none;'" : '';?>>	<form method="post" action="XXX">		<input type="text" class="big" name="post_title" value="<?php echo $post["Heading"]; ?>" placeholder="<?php echo _t("title"); ?>"/>		<?php run_action("wysiwyg", array("type" => "normal", "value" => $post["content"])); ?>		<input type="text" name="keywords" value="<?php echo $post["keywords"]; ?>" placeholder="<?php echo _t("keywords"); ?>"/>		<select name="label">		<option>recommended</option>		<option>hot</option>		</select>	</form></div><?php}}?></div><form></form></div></div>